#!/usr/bin/env node
const pkg = require("./package.json");
try {
  if (process.argv) {
    process.argv.find((a, i) => {
      if (a.includes("-v") || a.includes("--version")) {
        console.log(`version: ${pkg.version}`);
      } else if (a.includes("-update") || a.includes("-u")) {
        var exec = require("child_process").exec;
        exec("npm i -g bcw");
      }
    });
  }
} catch (error) {
  console.error(error);
}
